import React, { useState, useEffect } from "react";
import axios from "axios";

const Estoque = () => {
  const [produtos, setProdutos] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Função para buscar produtos e suas quantidades no estoque
    const fetchProdutos = async () => {
      try {
        const response = await axios.get("http://localhost:4321/api/products", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setProdutos(response.data);
      } catch (error) {
        console.error("Erro ao buscar produtos:", error);
        setError("Erro ao buscar produtos");
      }
    };

    fetchProdutos();
  }, []);

  // Função para criar um pedido de reabastecimento
  const handleReabastecimento = async (productId) => {
    try {
      const response = await axios.post(
        `http://localhost:4321/api/orders/create-replenishment`,
        { productId },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      setMessage(response.data.message);
      // Atualiza a lista de produtos após o reabastecimento
      const produtosAtualizados = await axios.get(
        "http://localhost:4321/api/products",
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setProdutos(produtosAtualizados.data);
    } catch (error) {
      console.error("Erro ao criar pedido de reabastecimento:", error);
      setError("Erro ao criar pedido de reabastecimento");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Gerenciamento de Estoque</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      {message && <div className="alert alert-success">{message}</div>}

      <table className="table mt-3">
        <thead>
          <tr>
            <th>Produto</th>
            <th>Quantidade em Estoque</th>
            <th>Quantidade Mínima</th>
            <th>Quantidade Máxima</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos.map((produto) => (
            <tr key={produto.id}>
              <td>{produto.name}</td>
              <td>{produto.stockQuantity}</td>
              <td>{produto.minQuantity}</td>
              <td>{produto.maxQuantity}</td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={() => handleReabastecimento(produto.id)}
                >
                  Reabastecer Estoque
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Estoque;
